import React, { useState, useEffect, useRef, useCallback } from 'react';
import styled from 'styled-components';
import { 
  ResponsiveContainer, 
  ComposedChart, 
  Line, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend,
  Scatter,
  ReferenceLine,
  ReferenceArea
} from 'recharts';
import axios from 'axios';

const TimelineContainer = styled.div`
  width: 100%;
  height: 500px;
  margin: 0 0 20px 0;
  background-color: var(--card-background);
  border-radius: 8px;
  padding: 16px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
  position: relative;
  user-select: none; /* Prevent text selection */
`;

const TimelineHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
`;

const Title = styled.h3`
  margin: 0;
  color: var(--text-color);
`;

const Controls = styled.div`
  display: flex;
  gap: 8px;
`;

const SelectionControls = styled.div`
  display: flex;
  align-items: center;
  margin-left: 20px;
`;

const SelectionModeButton = styled.button`
  background-color: ${props => props.active ? 'var(--accent-color)' : 'var(--button-background)'};
  color: ${props => props.active ? 'var(--button-text-active)' : 'var(--button-text)'};
  border: 1px solid var(--border-color);
  border-radius: 4px;
  padding: 4px 8px;
  margin-right: 8px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${props => props.active ? 'var(--accent-color)' : 'var(--button-hover-background)'};
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px var(--focus-color);
  }
`;

const ClearButton = styled.button`
  background-color: var(--button-background);
  color: var(--button-text);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  padding: 4px 8px;
  margin-right: 8px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: var(--button-hover-background);
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px var(--focus-color);
  }
`;

const ChartContainer = styled.div`
  width: 100%;
  height: calc(100% - 60px); /* Subtract header height */
  position: relative;
`;

const SelectionInfo = styled.div`
  margin-top: 10px;
  font-size: 14px;
  color: var(--text-secondary);
`;

// Mock data - this would come from your API
const generateMockData = (days = 30, symbol = 'GENERAL') => {
  const data = [];
  const startDate = new Date();
  startDate.setDate(startDate.getDate() - days);

  // Seed for pseudo-random but consistent data for each symbol
  const symbolSeed = symbol.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);

  let basePrice = 100 + (symbolSeed % 200); // Different starting price based on symbol
  let trend = (symbolSeed % 10) / 10; // Different trend direction based on symbol

  for (let i = 0; i < days; i++) {
    const date = new Date(startDate);
    date.setDate(date.getDate() + i);

    // Generate price movement influenced by the symbol and day
    const dayInfluence = Math.sin(i * (symbolSeed % 5) / 10) * 5;
    const randomFactor = Math.random() * 10 - 5;
    const priceChange = (randomFactor + dayInfluence + trend) * (1 + (symbolSeed % 5) / 10);

    basePrice += priceChange;

    const price = Math.max(50, basePrice); // Ensure price doesn't go too low
    const volume = Math.random() * 1000000 * (1 + (symbolSeed % 3) / 5);
    const sentiment = Math.random() * 2 - 1; // -1 to 1

    // Generate some news events randomly, but more likely on big price changes
    const priceChangeThreshold = Math.abs(priceChange) > 3 ? 0.5 : 0.8;
    const hasNews = Math.random() > priceChangeThreshold;
    const newsCount = hasNews ? Math.floor(Math.random() * 3) + 1 : 0;

    data.push({
      date: date.toISOString().split('T')[0],
      price,
      volume,
      sentiment,
      newsCount,
      news: hasNews ? {
        id: `news-${symbol}-${i}`,
        title: `${Math.abs(priceChange) > 3 ? 'Major' : 'Minor'} Movement in ${symbol}`,
        sentiment: sentiment
      } : null
    });
  }

  return data;
};

const Timeline = ({ dataSource, symbol, onDateSelect, onDateRangeSelect }) => {
  const [timeRange, setTimeRange] = useState('1M');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Selection state
  const [selectionMode, setSelectionMode] = useState('point'); // 'point' or 'range'
  const [selectedDate, setSelectedDate] = useState(null);
  const [rangeStart, setRangeStart] = useState(null);
  const [rangeEnd, setRangeEnd] = useState(null);
  const [rangeSelectionInProgress, setRangeSelectionInProgress] = useState(false);

  // Refs
  const chartRef = useRef(null);
  const chartContainerRef = useRef(null);

  // Fetch data
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        // Get days based on selected time range
        const days = timeRange === '1W' ? 7 : timeRange === '1M' ? 30 : timeRange === '3M' ? 90 : 365;

        // Try to fetch from API first
        const apiSymbol = symbol || 'GENERAL';
        const response = await axios.get(`http://localhost:8001/api/timeline?days=${days}&symbol=${apiSymbol}`);
        setData(response.data);
      } catch (err) {
        console.error('Error fetching timeline data:', err);
        setError('Failed to fetch data from API. Using mock data instead.');

        // Fallback to mock data if API fails
        const days = timeRange === '1W' ? 7 : timeRange === '1M' ? 30 : timeRange === '3M' ? 90 : 365;
        setData(generateMockData(days, symbol || 'GENERAL'));
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [dataSource, timeRange, symbol]);

  // Handle point selection
  const handlePointSelection = useCallback((date) => {
    console.log('Point selection:', date);
    setSelectedDate(date);
    setRangeStart(null);
    setRangeEnd(null);
    setRangeSelectionInProgress(false);

    if (onDateSelect) {
      onDateSelect(date);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  }, [onDateSelect, onDateRangeSelect]);

  // Handle first click of range selection
  const handleRangeStart = useCallback((date) => {
    console.log('Range selection start:', date);
    setSelectedDate(null);
    setRangeStart(date);
    setRangeEnd(null);
    setRangeSelectionInProgress(true);

    if (onDateSelect) {
      onDateSelect(null);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  }, [onDateSelect, onDateRangeSelect]);

  // Handle second click of range selection
  const handleRangeEnd = useCallback((date) => {
    console.log('Range selection end:', date);

    // Determine the correct start and end dates
    const start = new Date(rangeStart);
    const end = new Date(date);

    let startDate, endDate;

    if (start <= end) {
      startDate = rangeStart;
      endDate = date;
    } else {
      startDate = date;
      endDate = rangeStart;
    }

    setRangeEnd(date);
    setRangeSelectionInProgress(false);

    if (onDateRangeSelect) {
      onDateRangeSelect({ startDate, endDate });
    }
  }, [rangeStart, onDateRangeSelect]);

  // Handle mouse move during range selection
  const handleMouseMove = useCallback((e) => {
    if (!rangeSelectionInProgress || !chartContainerRef.current) return;

    try {
      // Get chart dimensions
      const chartRect = chartContainerRef.current.getBoundingClientRect();
      const chartX = e.clientX - chartRect.left;
      const chartWidth = chartRect.width;

      // Calculate position as percentage of chart width
      const positionPercent = Math.max(0, Math.min(1, chartX / chartWidth));

      // Find the closest data point to the mouse position
      if (data && data.length > 0) {
        const dateIndex = Math.floor(positionPercent * data.length);
        const clampedIndex = Math.max(0, Math.min(data.length - 1, dateIndex));
        const hoverDate = data[clampedIndex].date;
        setRangeEnd(hoverDate);
      }
    } catch (err) {
      console.error("Error during range selection:", err);
    }
  }, [rangeSelectionInProgress, data]);

  // Set up event listeners for mouse move
  useEffect(() => {
    if (!rangeSelectionInProgress) return;

    // Prevent default browser text selection during drag
    const preventDefaultSelection = (e) => {
      e.preventDefault();
      return false;
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('selectstart', preventDefaultSelection);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('selectstart', preventDefaultSelection);
    };
  }, [rangeSelectionInProgress, handleMouseMove]);

  // Handle chart click
  const handleChartClick = useCallback((event) => {
    if (!event || !event.activeLabel) return;

    const clickedDate = event.activeLabel;
    console.log('Chart clicked:', clickedDate, 'Mode:', selectionMode, 'In progress:', rangeSelectionInProgress);

    if (selectionMode === 'point') {
      handlePointSelection(clickedDate);
    } else if (selectionMode === 'range') {
      if (rangeSelectionInProgress) {
        handleRangeEnd(clickedDate);
      } else {
        handleRangeStart(clickedDate);
      }
    }
  }, [selectionMode, rangeSelectionInProgress, handlePointSelection, handleRangeStart, handleRangeEnd]);

  // Handle selection mode change
  const handleSelectionModeChange = useCallback((mode) => {
    setSelectionMode(mode);
    setSelectedDate(null);
    setRangeStart(null);
    setRangeEnd(null);
    setRangeSelectionInProgress(false);

    if (onDateSelect) {
      onDateSelect(null);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  }, [onDateSelect, onDateRangeSelect]);

  // Handle clear selection
  const handleClearSelection = useCallback(() => {
    setSelectedDate(null);
    setRangeStart(null);
    setRangeEnd(null);
    setRangeSelectionInProgress(false);

    if (onDateSelect) {
      onDateSelect(null);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  }, [onDateSelect, onDateRangeSelect]);

  // Get reference area coordinates for visualization
  const getReferenceAreaCoordinates = useCallback(() => {
    if (!rangeStart || !rangeEnd) return null;

    const dateA = new Date(rangeStart);
    const dateB = new Date(rangeEnd);

    if (dateA <= dateB) {
      return { x1: rangeStart, x2: rangeEnd };
    } else {
      return { x1: rangeEnd, x2: rangeStart };
    }
  }, [rangeStart, rangeEnd]);

  return (
    <TimelineContainer>
      <TimelineHeader>
        <Title>Market Timeline {symbol && `- ${symbol}`} {loading && '(Loading...)'}</Title>
        <Controls>
          <SelectionControls>
            <SelectionModeButton 
              active={selectionMode === 'point'} 
              onClick={() => handleSelectionModeChange('point')}
            >
              Point
            </SelectionModeButton>
            <SelectionModeButton 
              active={selectionMode === 'range'} 
              onClick={() => handleSelectionModeChange('range')}
            >
              Range
            </SelectionModeButton>
            <ClearButton onClick={handleClearSelection}>
              Clear
            </ClearButton>
          </SelectionControls>
        </Controls>
      </TimelineHeader>

      {error && (
        <div style={{ color: 'orange', marginBottom: '10px' }}>{error}</div>
      )}

      <ChartContainer ref={chartContainerRef}>
        <ResponsiveContainer width="100%" height="100%">
          <ComposedChart
            data={data}
            margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
            onClick={handleChartClick}
            ref={chartRef}
          >
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
            <XAxis 
              dataKey="date" 
              scale="auto" 
              tick={{ fill: 'var(--text-secondary)' }}
              tickLine={{ stroke: 'var(--text-secondary)' }}
            />
            <YAxis 
              yAxisId="left"
              orientation="left"
              tick={{ fill: 'var(--text-secondary)' }}
              tickLine={{ stroke: 'var(--text-secondary)' }}
              label={{ 
                value: 'Price ($)', 
                angle: -90, 
                position: 'insideLeft',
                fill: 'var(--text-secondary)'
              }}
            />
            <YAxis 
              yAxisId="right"
              orientation="right"
              tick={{ fill: 'var(--text-secondary)' }}
              tickLine={{ stroke: 'var(--text-secondary)' }}
              label={{ 
                value: 'Volume', 
                angle: 90, 
                position: 'insideRight',
                fill: 'var(--text-secondary)'
              }}
            />
            <Tooltip 
              contentStyle={{ 
                backgroundColor: 'var(--tooltip-background)',
                borderColor: 'var(--border-color)',
                color: 'var(--text-primary)'
              }}
            />
            <Legend 
              wrapperStyle={{ 
                color: 'var(--text-secondary)'
              }} 
            />
            <Bar 
              dataKey="volume" 
              fill="var(--volume-color)" 
              opacity={0.5} 
              yAxisId="right" 
            />
            <Line 
              type="monotone" 
              dataKey="price" 
              stroke="var(--price-color)" 
              strokeWidth={2}
              dot={false}
              yAxisId="left" 
            />
            <Line 
              type="monotone" 
              dataKey="sentiment" 
              stroke="var(--sentiment-color)" 
              strokeWidth={2}
              dot={false}
              yAxisId="left" 
            />
            <Scatter 
              dataKey="newsCount" 
              fill="var(--news-color)" 
              yAxisId="left"
              shape={(props) => {
                const { cx, cy } = props;
                return props.payload.newsCount > 0 ? (
                  <circle cx={cx} cy={cy} r={4} fill="var(--news-color)" />
                ) : null;
              }}
            />

            {/* Point selection indicator */}
            {selectedDate && (
              <ReferenceLine 
                x={selectedDate} 
                stroke="var(--selection-color)" 
                strokeWidth={2} 
              />
            )}

            {/* Range selection indicator */}
            {rangeStart && rangeEnd && getReferenceAreaCoordinates() && (
              <ReferenceArea 
                {...getReferenceAreaCoordinates()} 
                fill="var(--selection-color)" 
                fillOpacity={0.3} 
                stroke="var(--selection-color)"
                strokeWidth={1}
              />
            )}
          </ComposedChart>
        </ResponsiveContainer>
      </ChartContainer>

      <SelectionInfo>
        {selectedDate && (
          <div>Selected Date: <strong>{selectedDate}</strong></div>
        )}
        {rangeStart && rangeEnd && getReferenceAreaCoordinates() && (
          <div>
            Selected Range: <strong>{getReferenceAreaCoordinates().x1}</strong> to <strong>{getReferenceAreaCoordinates().x2}</strong>
          </div>
        )}
      </SelectionInfo>
    </TimelineContainer>
  );
};

export default Timeline;
