import React, { useState } from 'react';
import styled from 'styled-components';
import Timeline from '../components/Timeline';
import NewsFeed from '../components/NewsFeed';

const DashboardContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
`;

const TimelineSection = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const TimelineHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
`;

const DataSourceSelector = styled.div`
  display: flex;
  gap: 10px;
`;

const SelectorButton = styled.button`
  background-color: ${props => props.active ? 'var(--primary-color)' : 'var(--card-background)'};
  color: var(--text-color);
  border: 1px solid var(--border-color);
  padding: 8px 16px;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: ${props => props.active ? 'var(--primary-color)' : '#444'};
  }
`;

const NewsSection = styled.div`
  width: 100%;
`;

const DeepDiveSection = styled.div`
  width: 100%;
  background-color: var(--card-background);
  border-radius: 8px;
  padding: 30px;
  text-align: center;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
`;

const DeepDivePrompt = styled.p`
  font-size: 1.2rem;
  color: #aaa;
  margin: 0;
`;

const NewsModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background-color: var(--card-background);
  border-radius: 8px;
  padding: 24px;
  width: 80%;
  max-width: 800px;
  max-height: 80vh;
  overflow-y: auto;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.5);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 16px;
`;

const ModalTitle = styled.h2`
  margin: 0;
  color: var(--text-color);
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: var(--text-color);
  font-size: 1.5rem;
  cursor: pointer;
  padding: 0;

  &:hover {
    color: var(--primary-color);
  }
`;

const NewsSource = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 0.9rem;
  color: #aaa;
  margin-bottom: 16px;
`;

const NewsSummary = styled.div`
  margin-bottom: 24px;
  line-height: 1.6;
`;

const SentimentAnalysis = styled.div`
  background-color: rgba(45, 45, 45, 0.5);
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 24px;
`;

const SentimentHeader = styled.h3`
  margin-top: 0;
  margin-bottom: 12px;
  color: var(--text-color);
`;

const SentimentMeter = styled.div`
  height: 8px;
  background-color: #444;
  border-radius: 4px;
  margin-bottom: 8px;
  position: relative;
  overflow: hidden;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    height: 100%;
    width: ${props => Math.abs(props.sentiment * 50)}%;
    background-color: ${props => props.sentiment > 0 ? 'var(--positive-color)' : 'var(--negative-color)'};
    transform: ${props => props.sentiment > 0 ? 'translateX(0)' : 'translateX(-100%)'};
  }
`;

const SentimentLabel = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 0.8rem;
  color: #aaa;
`;

const Dashboard = () => {
  const [selectedNews, setSelectedNews] = useState(null);
  const [dataSource, setDataSource] = useState('stock');

  const handleNewsClick = (newsItem) => {
    setSelectedNews(newsItem);
  };

  const closeModal = () => {
    setSelectedNews(null);
  };

  return (
    <DashboardContainer>
      <TimelineSection>
        <TimelineHeader>
          <h2>Market Timeline</h2>
          <DataSourceSelector>
            <SelectorButton 
              active={dataSource === 'stock'} 
              onClick={() => setDataSource('stock')}
            >
              Stock
            </SelectorButton>
            <SelectorButton 
              active={dataSource === 'etf'} 
              onClick={() => setDataSource('etf')}
            >
              ETF
            </SelectorButton>
            <SelectorButton 
              active={dataSource === 'csv'} 
              onClick={() => setDataSource('csv')}
            >
              Upload CSV
            </SelectorButton>
          </DataSourceSelector>
        </TimelineHeader>
        <Timeline />
      </TimelineSection>

      <NewsSection>
        <NewsFeed onNewsClick={handleNewsClick} />
      </NewsSection>

      <DeepDiveSection>
        <DeepDivePrompt>
          Click a point on the timeline to see a breakdown of market context and sentiment.
        </DeepDivePrompt>
      </DeepDiveSection>

      {selectedNews && (
        <NewsModal onClick={closeModal}>
          <ModalContent onClick={e => e.stopPropagation()}>
            <ModalHeader>
              <ModalTitle>{selectedNews.title}</ModalTitle>
              <CloseButton onClick={closeModal}>&times;</CloseButton>
            </ModalHeader>

            <NewsSource>
              <span>{selectedNews.source}</span>
              <span>{selectedNews.date}</span>
            </NewsSource>

            <NewsSummary>
              {selectedNews.summary}
            </NewsSummary>

            <SentimentAnalysis>
              <SentimentHeader>Sentiment Analysis</SentimentHeader>
              <SentimentMeter sentiment={selectedNews.sentiment} />
              <SentimentLabel>
                <span>Negative</span>
                <span>Neutral</span>
                <span>Positive</span>
              </SentimentLabel>
            </SentimentAnalysis>
          </ModalContent>
        </NewsModal>
      )}
    </DashboardContainer>
  );
};

export default Dashboard;
