import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const NewsFeedContainer = styled.div`
  width: 100%;
  background-color: var(--card-background);
  border-radius: 8px;
  padding: 16px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
  margin-bottom: 20px;
  max-width: 100%;
`;

const NewsFeedHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
`;

const Title = styled.h3`
  margin: 0;
  color: var(--text-color);
`;

const NewsItemList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
  max-height: 400px;
  overflow-y: auto;
`;

const NewsItem = styled.div`
  padding: 12px;
  border-bottom: 1px solid var(--border-color);
  cursor: pointer;
  transition: background-color 0.2s ease;
  background-color: ${props => 
    props.highlighted 
      ? 'var(--highlight-background)' 
      : 'transparent'
  };
  border-left: ${props => 
    props.highlighted 
      ? '4px solid var(--accent-color)' 
      : '4px solid transparent'
  };

  &:hover {
    background-color: var(--hover-background);
  }

  &:last-child {
    border-bottom: none;
  }
`;

const NewsContent = styled.div`
  flex: 1;
`;

const NewsTitle = styled.h4`
  margin: 0 0 8px 0;
  color: var(--text-color);
`;

const NewsSource = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 0.8rem;
  color: #aaa;
`;

const NewsMeta = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  justify-content: space-between;
  min-width: 80px;
`;

const NewsDate = styled.div`
  font-size: 0.8rem;
  color: #aaa;
`;

const SentimentBadge = styled.div`
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 0.8rem;
  font-weight: bold;
  background-color: ${props => 
    props.sentiment > 0.3 ? 'rgba(76, 175, 80, 0.2)' : 
    props.sentiment < -0.3 ? 'rgba(244, 67, 54, 0.2)' : 
    'rgba(158, 158, 158, 0.2)'};
  color: ${props => 
    props.sentiment > 0.3 ? 'var(--positive-color)' : 
    props.sentiment < -0.3 ? 'var(--negative-color)' : 
    'var(--neutral-color)'};
`;

const FilterDropdown = styled.div`
  display: flex;
  align-items: center;
  margin-left: 20px;
`;

const FilterLabel = styled.span`
  font-size: 14px;
  margin-right: 8px;
  color: var(--text-secondary);
`;

const FilterSelect = styled.select`
  background-color: var(--input-background);
  color: var(--text-primary);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  padding: 4px 8px;
  font-size: 14px;
  cursor: pointer;

  &:focus {
    outline: none;
    border-color: var(--accent-color);
  }
`;

// Mock data - this would come from your API
const generateMockNewsData = (symbol = 'GENERAL') => {
  // Seed for pseudo-random but consistent data for each symbol
  const symbolSeed = symbol.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);

  const newsSources = ["Bloomberg", "CNBC", "Financial Times", "Wall Street Journal", "Reuters"];

  // Generate news titles based on the symbol
  const symbolName = symbol === 'GENERAL' ? 'Markets' : symbol;

  const positiveEvents = [
    `${symbolName} Surges on Strong Earnings Report`,
    `Analysts Upgrade ${symbolName} Following Product Announcement`,
    `${symbolName} Gains as Sector Shows Growth`,
    `New Partnership Boosts ${symbolName} Shares`,
    `${symbolName} Rallies Amid Positive Economic Data`
  ];

  const negativeEvents = [
    `${symbolName} Drops After Missing Quarterly Expectations`,
    `Regulatory Concerns Weigh on ${symbolName}`,
    `${symbolName} Falls as Competitors Gain Market Share`,
    `Investors Cautious About ${symbolName}'s Growth Prospects`,
    `${symbolName} Declines Following Industry Downturn`
  ];

  const neutralEvents = [
    `${symbolName} Holds Steady Ahead of Earnings`,
    `Analysts Maintain Neutral Stance on ${symbolName}`,
    `${symbolName} Shows Mixed Signals in Volatile Market`,
    `Investors Await More Clarity on ${symbolName}'s Strategy`,
    `${symbolName} Trades Sideways on Low Volume`
  ];

  // General market news that might affect any symbol
  const marketEvents = [
    "Federal Reserve Signals Potential Rate Hike",
    "Inflation Concerns Weigh on Markets",
    "Global Supply Chain Issues Begin to Ease",
    "Economic Growth Exceeds Expectations",
    "Market Volatility Increases Amid Geopolitical Tensions"
  ];

  // Generate news items
  const newsCount = 5 + (symbolSeed % 5); // 5-9 news items
  const data = [];

  const today = new Date();

  for (let i = 0; i < newsCount; i++) {
    // Date within the last 10 days
    const daysAgo = Math.floor(Math.random() * 10);
    const date = new Date(today);
    date.setDate(date.getDate() - daysAgo);

    // Sentiment based on symbol and day
    const sentimentBase = ((symbolSeed + i) % 10) / 10; // 0 to 0.9
    const sentiment = (sentimentBase * 2 - 1) + (Math.random() * 0.4 - 0.2); // -1 to 1 with some randomness

    // Select news title based on sentiment
    let title;
    let isSymbolSpecific = Math.random() > 0.3; // 70% symbol-specific news, 30% general market news

    if (!isSymbolSpecific) {
      title = marketEvents[Math.floor(Math.random() * marketEvents.length)];
    } else if (sentiment > 0.3) {
      title = positiveEvents[Math.floor(Math.random() * positiveEvents.length)];
    } else if (sentiment < -0.3) {
      title = negativeEvents[Math.floor(Math.random() * negativeEvents.length)];
    } else {
      title = neutralEvents[Math.floor(Math.random() * neutralEvents.length)];
    }

    data.push({
      id: i + 1,
      title,
      source: newsSources[Math.floor(Math.random() * newsSources.length)],
      date: date.toISOString().split('T')[0],
      sentiment,
      summary: `This is a mock summary about ${isSymbolSpecific ? symbolName : 'the market'} on ${date.toISOString().split('T')[0]}.`,
      isSymbolSpecific
    });
  }

  // Sort by date (newest first)
  return data.sort((a, b) => new Date(b.date) - new Date(a.date));
};

const NewsFeed = ({ 
  onNewsClick, 
  symbol = 'GENERAL', 
  dataSource,
  selectedDate = null,
  selectedDateRange = null
}) => {
  const [newsData, setNewsData] = useState([]);
  const [filteredNewsData, setFilteredNewsData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [filterType, setFilterType] = useState('all'); // 'all', 'relative', 'date-specific'
  const newsRefs = useRef({});

  useEffect(() => {
    const fetchNews = async () => {
      setLoading(true);
      setError(null);

      try {
        // Try to fetch from API first
        const apiSymbol = symbol || 'GENERAL';
        const response = await axios.get(`http://localhost:8001/api/news?symbol=${apiSymbol}`);
        setNewsData(response.data);
      } catch (err) {
        console.error('Error fetching news data:', err);
        setError('Failed to fetch news from API. Using mock data instead.');

        // Fallback to mock data if API fails
        setNewsData(generateMockNewsData(symbol));
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [symbol, dataSource]);

  // Filter news based on selected date/range and filter type
  useEffect(() => {
    if (!newsData.length) return;

    let filtered = [...newsData];

    if (filterType === 'date-specific' && selectedDate) {
      filtered = newsData.filter(news => news.date === selectedDate);
    } else if (filterType === 'relative' && selectedDateRange) {
      const { startDate, endDate } = selectedDateRange;
      filtered = newsData.filter(news => {
        const newsDate = new Date(news.date);
        return newsDate >= new Date(startDate) && newsDate <= new Date(endDate);
      });
    }

    setFilteredNewsData(filtered);

    // Scroll to the first news item that matches the selected date
    if (selectedDate && filterType === 'all') {
      setTimeout(() => {
        const matchingNews = newsData.find(news => news.date === selectedDate);
        if (matchingNews && newsRefs.current[matchingNews.id]) {
          newsRefs.current[matchingNews.id].scrollIntoView({ 
            behavior: 'smooth',
            block: 'start'
          });
        }
      }, 100);
    }
  }, [newsData, selectedDate, selectedDateRange, filterType]);

  const handleNewsClick = (newsItem) => {
    if (onNewsClick) {
      onNewsClick(newsItem);
    }
  };

  const handleFilterChange = (e) => {
    setFilterType(e.target.value);
  };

  const getSentimentLabel = (sentiment) => {
    if (sentiment > 0.3) return 'Positive';
    if (sentiment < -0.3) return 'Negative';
    return 'Neutral';
  };

  return (
    <NewsFeedContainer>
      <NewsFeedHeader>
        <Title>Market News {symbol !== 'GENERAL' && `for ${symbol}`} {loading && '(Loading...)'}</Title>
        <FilterDropdown>
          <FilterLabel>Filter:</FilterLabel>
          <FilterSelect value={filterType} onChange={handleFilterChange}>
            <option value="all">All News (Scroll to Selected)</option>
            <option value="relative">Selected Date Range Only</option>
            <option value="date-specific">Selected Date Only</option>
          </FilterSelect>
        </FilterDropdown>
      </NewsFeedHeader>

      {error && (
        <div style={{ color: 'orange', marginBottom: '10px' }}>{error}</div>
      )}

      <NewsItemList>
        {(filteredNewsData.length > 0 ? filteredNewsData : newsData).map(news => (
          <NewsItem 
            key={news.id} 
            sentiment={news.sentiment}
            onClick={() => handleNewsClick(news)}
            ref={el => newsRefs.current[news.id] = el}
            highlighted={selectedDate === news.date}
          >
            <NewsContent>
              <NewsTitle>{news.title}</NewsTitle>
              <NewsSource>
                <span>{news.source}</span>
              </NewsSource>
            </NewsContent>
            <NewsMeta>
              <NewsDate>{news.date}</NewsDate>
              <SentimentBadge sentiment={news.sentiment}>
                {getSentimentLabel(news.sentiment)}
              </SentimentBadge>
            </NewsMeta>
          </NewsItem>
        ))}

        {filteredNewsData.length === 0 && filterType !== 'all' && (
          <div style={{ padding: '20px', textAlign: 'center', color: 'var(--text-secondary)' }}>
            No news articles found for the selected {filterType === 'date-specific' ? 'date' : 'date range'}.
          </div>
        )}
      </NewsItemList>
    </NewsFeedContainer>
  );
};

export default NewsFeed;
