import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { 
  ResponsiveContainer, 
  ComposedChart, 
  Line, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend,
  Scatter,
  ReferenceLine,
  ReferenceArea
} from 'recharts';
import axios from 'axios';

const TimelineContainer = styled.div`
  width: 100%;
  height: 500px;
  margin: 0 0 20px 0;
  background-color: var(--card-background);
  border-radius: 8px;
  padding: 16px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
  position: relative;
`;

const TimelineHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
`;

const Title = styled.h3`
  margin: 0;
  color: var(--text-color);
`;

const Controls = styled.div`
  display: flex;
  gap: 8px;
`;

const Button = styled.button`
  background-color: ${props => props.active ? 'var(--primary-color)' : 'var(--card-background)'};
  color: var(--text-color);
  border: 1px solid var(--border-color);
  padding: 4px 8px;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: ${props => props.active ? 'var(--primary-color)' : '#444'};
  }
`;

const SelectionControls = styled.div`
  display: flex;
  align-items: center;
  margin-left: 20px;
`;

const SelectionButton = styled.button`
  background-color: ${props => props.active ? 'var(--accent-color)' : 'var(--button-background)'};
  color: ${props => props.active ? 'var(--button-text-active)' : 'var(--button-text)'};
  border: 1px solid var(--border-color);
  border-radius: 4px;
  padding: 4px 8px;
  margin-right: 8px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${props => props.active ? 'var(--accent-color)' : 'var(--button-hover-background)'};
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px var(--focus-color);
  }
`;

const SelectionLabel = styled.span`
  font-size: 14px;
  margin-right: 8px;
  color: var(--text-secondary);
`;

const ChartOverlay = styled.div`
  position: absolute;
  top: 80px; /* Adjust based on your header height */
  left: 16px;
  right: 16px;
  bottom: 16px;
  pointer-events: none;
  z-index: 10;
`;

// Mock data - this would come from your API
const generateMockData = (days = 30, symbol = 'GENERAL') => {
  const data = [];
  const startDate = new Date();
  startDate.setDate(startDate.getDate() - days);

  // Seed for pseudo-random but consistent data for each symbol
  const symbolSeed = symbol.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);

  let basePrice = 100 + (symbolSeed % 200); // Different starting price based on symbol
  let trend = (symbolSeed % 10) / 10; // Different trend direction based on symbol

  for (let i = 0; i < days; i++) {
    const date = new Date(startDate);
    date.setDate(date.getDate() + i);

    // Generate price movement influenced by the symbol and day
    const dayInfluence = Math.sin(i * (symbolSeed % 5) / 10) * 5;
    const randomFactor = Math.random() * 10 - 5;
    const priceChange = (randomFactor + dayInfluence + trend) * (1 + (symbolSeed % 5) / 10);

    basePrice += priceChange;

    const price = Math.max(50, basePrice); // Ensure price doesn't go too low
    const volume = Math.random() * 1000000 * (1 + (symbolSeed % 3) / 5);
    const sentiment = Math.random() * 2 - 1; // -1 to 1

    // Generate some news events randomly, but more likely on big price changes
    const priceChangeThreshold = Math.abs(priceChange) > 3 ? 0.5 : 0.8;
    const hasNews = Math.random() > priceChangeThreshold;
    const newsCount = hasNews ? Math.floor(Math.random() * 3) + 1 : 0;

    data.push({
      date: date.toISOString().split('T')[0],
      price,
      volume,
      sentiment,
      newsCount,
      news: hasNews ? {
        id: `news-${symbol}-${i}`,
        title: `${Math.abs(priceChange) > 3 ? 'Major' : 'Minor'} Movement in ${symbol}`,
        sentiment: sentiment
      } : null
    });
  }

  return data;
};

const Timeline = ({ dataSource, symbol, onDateSelect, onDateRangeSelect }) => {
  const [timeRange, setTimeRange] = useState('1M');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectionMode, setSelectionMode] = useState('point'); // 'point' or 'range'
  const [selectionStart, setSelectionStart] = useState(null);
  const [selectionEnd, setSelectionEnd] = useState(null);
  const [isSelecting, setIsSelecting] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [dragStartX, setDragStartX] = useState(null);
  const chartRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        // Get days based on selected time range
        const days = timeRange === '1W' ? 7 : timeRange === '1M' ? 30 : timeRange === '3M' ? 90 : 365;

        // Try to fetch from API first
        const apiSymbol = symbol || 'GENERAL';
        const response = await axios.get(`http://localhost:8001/api/timeline?days=${days}&symbol=${apiSymbol}`);
        setData(response.data);
      } catch (err) {
        console.error('Error fetching timeline data:', err);
        setError('Failed to fetch data from API. Using mock data instead.');

        // Fallback to mock data if API fails
        const days = timeRange === '1W' ? 7 : timeRange === '1M' ? 30 : timeRange === '3M' ? 90 : 365;
        setData(generateMockData(days, symbol || 'GENERAL'));
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [timeRange, dataSource, symbol]);

  // Set up event listeners for drag selection
  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    const handleMouseMove = (e) => {
      if (!isDragging || !chartRef.current) return;

      // Get chart dimensions
      const chartRect = chartRef.current.getBoundingClientRect();
      const chartX = e.clientX - chartRect.left;

      // Find the closest data point to the mouse position
      const xScale = chartRef.current.state.xAxisMap.left.scale;
      const domain = xScale.domain();
      const range = xScale.range();

      // Calculate the position in the domain based on the mouse position
      const domainPosition = (chartX - range[0]) / (range[1] - range[0]);
      const dateIndex = Math.round(domainPosition * (domain.length - 1));

      if (dateIndex >= 0 && dateIndex < domain.length) {
        const hoverDate = domain[dateIndex];
        setSelectionEnd(hoverDate);
      }
    };

    const handleMouseUp = (e) => {
      if (isDragging && selectionStart && selectionEnd) {
        // Finalize the selection
        const dateA = new Date(selectionStart);
        const dateB = new Date(selectionEnd);

        let startDate, endDate;

        if (dateA < dateB) {
          startDate = selectionStart;
          endDate = selectionEnd;
        } else {
          startDate = selectionEnd;
          endDate = selectionStart;
        }

        if (onDateRangeSelect) {
          onDateRangeSelect({ startDate, endDate });
        }
      } else if (isDragging && selectionStart) {
        // If dragged very little, treat as a click
        setSelectedDate(selectionStart);
        setSelectionStart(null);
        setSelectionEnd(null);

        if (onDateSelect) {
          onDateSelect(selectionStart);
        }
      }

      setIsDragging(false);
      setDragStartX(null);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, selectionStart, selectionEnd, onDateSelect, onDateRangeSelect]);

  const handleTimeRangeChange = (range) => {
    setTimeRange(range);
    // Reset selections when time range changes
    setSelectedDate(null);
    setSelectionStart(null);
    setSelectionEnd(null);

    if (onDateSelect) {
      onDateSelect(null);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  };

  const handleChartClick = (event) => {
    if (!event || !event.activeLabel) return;

    const clickedDate = event.activeLabel;

    if (selectionMode === 'point') {
      // Simple point selection
      setSelectedDate(clickedDate);
      setSelectionStart(null);
      setSelectionEnd(null);

      if (onDateSelect) {
        onDateSelect(clickedDate);
      }

      if (onDateRangeSelect) {
        onDateRangeSelect(null);
      }
    } else if (selectionMode === 'range') {
      // Start drag selection
      setIsDragging(true);
      setSelectionStart(clickedDate);
      setSelectionEnd(null);
      setSelectedDate(null);

      if (onDateSelect) {
        onDateSelect(null);
      }
    }
  };

  const handleSelectionModeChange = (mode) => {
    setSelectionMode(mode);

    // Reset selections when mode changes
    setSelectedDate(null);
    setSelectionStart(null);
    setSelectionEnd(null);
    setIsSelecting(false);

    if (onDateSelect) {
      onDateSelect(null);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  };

  const handleClearSelection = () => {
    setSelectedDate(null);
    setSelectionStart(null);
    setSelectionEnd(null);
    setIsSelecting(false);

    if (onDateSelect) {
      onDateSelect(null);
    }

    if (onDateRangeSelect) {
      onDateRangeSelect(null);
    }
  };

  // Get the correct start and end dates for the reference area
  const getReferenceAreaCoordinates = () => {
    if (!selectionStart || !selectionEnd) return null;

    const dateA = new Date(selectionStart);
    const dateB = new Date(selectionEnd);

    if (dateA < dateB) {
      return { x1: selectionStart, x2: selectionEnd };
    } else {
      return { x1: selectionEnd, x2: selectionStart };
    }
  };

  return (
    <TimelineContainer ref={containerRef}>
      <TimelineHeader>
        <Title>Market Timeline {symbol && `- ${symbol}`} {loading && '(Loading...)'}</Title>
        <Controls>
          <Button active={timeRange === '1W'} onClick={() => handleTimeRangeChange('1W')}>1W</Button>
          <Button active={timeRange === '1M'} onClick={() => handleTimeRangeChange('1M')}>1M</Button>
          <Button active={timeRange === '3M'} onClick={() => handleTimeRangeChange('3M')}>3M</Button>
          <Button active={timeRange === '1Y'} onClick={() => handleTimeRangeChange('1Y')}>1Y</Button>
        </Controls>
        <SelectionControls>
          <SelectionLabel>Selection:</SelectionLabel>
          <SelectionButton 
            active={selectionMode === 'point'} 
            onClick={() => handleSelectionModeChange('point')}
          >
            Point
          </SelectionButton>
          <SelectionButton 
            active={selectionMode === 'range'} 
            onClick={() => handleSelectionModeChange('range')}
          >
            Range
          </SelectionButton>
          {(selectedDate || selectionStart) && (
            <SelectionButton onClick={handleClearSelection}>
              Clear
            </SelectionButton>
          )}
        </SelectionControls>
      </TimelineHeader>

      {error && (
        <div style={{ color: 'orange', marginBottom: '10px' }}>{error}</div>
      )}

      <ResponsiveContainer width="100%" height="100%">
        <ComposedChart
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
          onClick={handleChartClick}
          ref={chartRef}
        >
          <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
          <XAxis 
            dataKey="date" 
            scale="auto" 
            tick={{ fill: 'var(--text-secondary)' }}
            tickLine={{ stroke: 'var(--text-secondary)' }}
          />
          <YAxis 
            yAxisId="left"
            orientation="left"
            tick={{ fill: 'var(--text-secondary)' }}
            tickLine={{ stroke: 'var(--text-secondary)' }}
            label={{ 
              value: 'Price ($)', 
              angle: -90, 
              position: 'insideLeft',
              fill: 'var(--text-secondary)'
            }}
          />
          <YAxis 
            yAxisId="right"
            orientation="right"
            tick={{ fill: 'var(--text-secondary)' }}
            tickLine={{ stroke: 'var(--text-secondary)' }}
            label={{ 
              value: 'Volume', 
              angle: 90, 
              position: 'insideRight',
              fill: 'var(--text-secondary)'
            }}
          />
          <Tooltip 
            contentStyle={{ 
              backgroundColor: 'var(--tooltip-background)',
              borderColor: 'var(--border-color)',
              color: 'var(--text-primary)'
            }}
          />
          <Legend 
            verticalAlign="top" 
            wrapperStyle={{ paddingBottom: 10 }}
          />
          <Bar 
            dataKey="volume" 
            yAxisId="right" 
            fill="rgba(100, 100, 255, 0.3)" 
            name="Volume"
          />
          <Line 
            type="monotone" 
            dataKey="price" 
            stroke="#8884d8" 
            yAxisId="left" 
            name="Price"
            dot={false}
            activeDot={{ r: 8 }}
          />
          <Line 
            type="monotone" 
            dataKey="sentiment" 
            stroke="#82ca9d" 
            yAxisId="left" 
            name="Sentiment"
            dot={false}
          />
          <Scatter 
            dataKey="newsCount" 
            fill="#ff7300" 
            yAxisId="left" 
            name="News Events"
            shape={(props) => {
              const { cx, cy } = props;
              return (
                <circle 
                  cx={cx} 
                  cy={cy} 
                  r={5} 
                  fill="#ff7300" 
                  stroke="none"
                />
              );
            }}
          />

          {/* Reference line for selected date */}
          {selectedDate && (
            <ReferenceLine 
              x={selectedDate} 
              yAxisId="left"
              stroke="var(--accent-color)" 
              strokeWidth={2}
              strokeDasharray="3 3"
              label={{ 
                value: 'Selected', 
                position: 'insideTopRight', 
                fill: 'var(--accent-color)'
              }}
            />
          )}

          {/* Reference area for selected range */}
          {selectionStart && selectionEnd && getReferenceAreaCoordinates() && (
            <ReferenceArea 
              {...getReferenceAreaCoordinates()}
              yAxisId="left"
              stroke="var(--accent-color)"
              strokeOpacity={0.3}
              fill="var(--accent-color)"
              fillOpacity={0.1}
            />
          )}

          {/* Reference line for selection in progress */}
          {selectionStart && isDragging && !selectionEnd && (
            <ReferenceLine 
              x={selectionStart} 
              yAxisId="left"
              stroke="var(--accent-color)" 
              strokeWidth={2}
              strokeDasharray="3 3"
            />
          )}
        </ComposedChart>
      </ResponsiveContainer>
    </TimelineContainer>
  );
};

export default Timeline;
